mapper.insert:
insert into member(
email,
password,
name
) values(
#{email},
#{password},
#{name}
)

mapper.update:
update member
set name=#{name}
where id=#{id};

mapper.insert:
insert into board(
title,
content,
m_id
) values(
#{title},
#{content},
#{m_id}
)

mapper.update:
update board
set title=#{title}, content=#{content}
where id=#{id};
